import { React, useLayoutEffect, useRef, useState } from "react";
import { NavLink } from "react-router-dom"
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
//styles
import '../styles/nav.css';

const Nav = ({ location }) => {
    const pathname = location.pathname;
    const [previousPath, setPreviousPath] = useState(pathname);
    gsap.registerPlugin(SplitText);
    let header = useRef(null);

    useLayoutEffect(() => {
        if (["/", "/about"].includes(pathname) && pathname !== previousPath) {

            let ctx = gsap.context(() => {
                const tl = gsap.timeline(),
                    mySplitText = new SplitText(header, { type: "chars" }),
                    chars = mySplitText.chars; //an array of all the divs that wrap each character

                tl.from(chars, {
                    duration: 0.4,
                    opacity: 0,
                    y: 100,
                    rotationX: 0,
                    ease: "ease",
                    stagger: 0.1
                });
            })
            return () => ctx.revert();

            console.log('yep');
        }
        else {

            console.log('nope');
        }
        setPreviousPath(location.pathname);
    }, [pathname, previousPath]);



    return (
        <nav className='nav'>
            <NavLink to="/" exact='true' className='nav-title' ref={el => header = el}>
                Jaime Castaneda
            </NavLink>
            <div className="nav-social">
                <a href="https://www.linkedin.com/in/jaime-castaneda-956154a8/">
                    Linkedin
                </a>
                <a href="https://www.instagram.com/the_casta_way/">
                    Instagram
                </a>
                <a href="https://twitter.com/the_casta_way">
                    Threads
                </a>
            </div>
            <div className="nav-links">
                <NavLink to="/showcase" exact='true' style={({ isActive, isPending }) => {
                    return {
                        color: isActive ? "Blue" : "#DCDCDC",
                    };
                }}>
                    Showcase
                </NavLink>
                <NavLink to="/about" exact='true' style={({ isActive, isPending }) => {
                    return {
                        color: isActive ? "Blue" : "#DCDCDC",
                    };
                }}>
                    About
                </NavLink>
                <a href="mailto:jccd.designs@gmail.com">
                    Contact
                </a>
            </div>
        </nav>
    );
}

export default Nav;
