import React, { useLayoutEffect, useRef, useState, ud } from 'react';
import { gsap } from "gsap";
import { SplitText } from "gsap/SplitText";
//styles
import '../../styles/loader.css';

const DURATION = 0.4;
const PERCENT_DELAY = -0.3;
const NUMBER_DELAY = 0.5;

const Loader = () => {
    const [progress, setProgress] = useState(0);

    //initialize gsap plugins
    gsap.registerPlugin(SplitText);

    //counter refs
    let loaderNumber100 = useRef(null);
    let loaderNumber88 = useRef(null);
    let loaderNumber22 = useRef(null);
    let loaderNumber0 = useRef(null);
    let loaderNumberPercent = useRef(null);
    //loader info refs
    let loaderInfo = useRef(null);

    useLayoutEffect(() => {
        let ctx = gsap.context(() => {
            //define timeline
            const tl = gsap.timeline(),
                loaderNumber22Split = new SplitText(loaderNumber22, { type: "chars" }),
                loaderNumber88Split = new SplitText(loaderNumber88, { type: "chars" }),
                loaderNumber100Split = new SplitText(loaderNumber100, { type: "chars" }),
                chars22 = loaderNumber22Split.chars,
                chars88 = loaderNumber88Split.chars,
                chars100 = loaderNumber100Split.chars;

            tl.from(loaderNumber0, {
                duration: DURATION,
                delay: 0.5,
                opacity: 0,
                y: 100,
                ease: "ease",
            });
            tl.from(loaderNumberPercent, {
                duration: DURATION,
                delay: PERCENT_DELAY,
                opacity: 0,
                y: 100,
                rotationX: 0,
                ease: "ease",
            });
            tl.to(loaderNumber0, {
                duration: DURATION,
                delay: NUMBER_DELAY,
                opacity: 0,
                y: -100,
                ease: "ease",
            });
            tl.from(chars22, {
                duration: DURATION,
                opacity: 0,
                y: 100,
                rotationX: 0,
                ease: "ease",
                stagger: 0.1
            });
            tl.to(chars22, {
                duration: DURATION,
                delay: NUMBER_DELAY,
                opacity: 0,
                y: -100,
                rotationX: 0,
                ease: "ease",
                stagger: 0.1
            });
            tl.from(chars88, {
                duration: DURATION,
                opacity: 0,
                y: 100,
                rotationX: 0,
                ease: "ease",
                stagger: 0.1
            });
            tl.to(chars88, {
                duration: DURATION,
                delay: NUMBER_DELAY,
                opacity: 0,
                y: -100,
                rotationX: 0,
                ease: "ease",
                stagger: 0.1
            });
            tl.from(chars100, {
                duration: DURATION,
                opacity: 0,
                y: 100,
                rotationX: 0,
                ease: "ease",
                stagger: 0.1
            });
            tl.to(chars100, {
                duration: DURATION,
                delay: NUMBER_DELAY,
                opacity: 0,
                y: -100,
                rotationX: 0,
                ease: "ease",
                stagger: 0.1
            });
            tl.to(loaderNumberPercent, {
                duration: DURATION,
                delay: PERCENT_DELAY,
                opacity: 0,
                y: -100,
                ease: "ease",
            });

        })

        return () => ctx.revert();

    })

    useLayoutEffect(() => {
        let ctx = gsap.context(() => {
            //define timeline
            const tl = gsap.timeline();

            tl.from(loaderInfo, {
                duration: DURATION,
                delay: 0.5,
                opacity: 0,
                y: 100,
                ease: "ease",
            });
            tl.to(loaderInfo, {
                duration: DURATION,
                delay: 5.5,
                opacity: 0,
                y: -100,
                ease: "ease",
            });

        })

        return () => ctx.revert();

    })

    return (
        <div id="loader" className='loader'>
            <div className='loader-container'>
                <p className='loader-counter'>
                    0%
                </p>
                <h1 className='loader-header'>
                    0%
                </h1>

            </div>
        </div >
    );
}

export default Loader;
