import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
//styles
import '../styles/header.css';
import '../styles/App.css';

const Link = ({ parent }) => {
    gsap.registerPlugin(SplitText);
    let linkButton = useRef(null);
    let linkButtonBorder = useRef(null);
    let linkButtonText = useRef(null);
    let linkButtonIcon = useRef(null);

    useEffect(() => {
        const linkTL = gsap.timeline();
        console.log(parent)
        // parent.addEventListener('mouseenter', () => { linkTL.play() })
        // parent.addEventListener('mouseleave', () => { linkTL.reverse() })
    }, []);


    // onClick = {() => { toggleModal(); }}

    return (
        <div ref={el => linkButton = el} className="nav-button"  >
            <div ref={el => linkButtonBorder = el} className="nav-button-border" />
            <div ref={el => linkButtonText = el} className="nav-button-text">
                <p>
                    Menu
                </p>
            </div>
            <div ref={el => linkButtonIcon = el} className="nav-button-icon">
                <div className="nav-button-icon-horizontal" />
            </div>
        </div>
    );
}

export default Link;
