import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
//components
//styles
import '../../styles/home.css';
//assets
import Headshot from '../../media/about/about_headshot.jpg'
import metaNews from '../../media/thumbnails/thumbnail_meta_news.webp'
import vr4g from '../../media/thumbnails/thumbnail_vr4g.webp'
import rbs from '../../media/thumbnails/thumbnail_rbs_uth.webp'
import rsvp from '../../media/thumbnails/thumbnail_rsvp.webp'

const Home = () => {
  //refs
  let home = useRef(null);
  let homeMarquee = useRef(null);


  useEffect(() => {
    gsap.registerPlugin(SplitText);
    const marquee = home.querySelectorAll('.home-marquee');
    const tl = gsap.timeline(),
      marqueeSplitText = new SplitText(marquee, { type: "words" }),
      marqueeWords = marqueeSplitText.words; //an array of all the divs that wrap each character

    tl.from(marqueeWords, {
      duration: 0.2,
      opacity: 0,
      y: '25%',
      ease: "ease",
      stagger: 0.05
    });
    tl.to(marquee, {
      duration: 30,
      x: '100%',
      ease: "power1.in",
      repeat: -1,
    });

  }, []);

  return (
    <div className='home' ref={el => home = el}>
      <div className='home-cards-container'>
        <div className='home-cards'>
          <div className='home-card'>
            <img className='about-headshot' src={Headshot} />
          </div>
          <div className='home-card'>
            <img className='about-headshot' src={Headshot} />
          </div>
          <div className='home-card'>
            <img className='about-headshot' src={Headshot} />
          </div>
          <div className='home-card'>
            <img className='about-headshot' src={Headshot} />
          </div>
        </div>
      </div>
      <div className='home-marquee-container'>
        <div ref={el => homeMarquee = el} className='home-marquee'>
          <span className='home-marquee-text'>
            Designer -
          </span>
          <span className='home-marquee-text'>
            Developer -
          </span>
          <span className='home-marquee-text'>
            UI Designer -
          </span>
          <span className='home-marquee-text'>
            Web Designer -
          </span>
        </div>
      </div>
    </div >
  );
}

export default Home;
