import React, { useEffect, useLayoutEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
import { Link } from "react-router-dom"
//components
import Location from '../../components/location.react';
import Instruction from '../../components/instruction.react';
//styles
import '../../styles/home.css';
//assets
import { Media } from "../../media/media";

const Home = () => {
  //state
  const [enabled, setEnabled] = useState(false);
  const [marqueeWidth, setMarqueeWidth] = useState(null | Number);
  //refs
  const homeMarquee = useRef(HTMLElement);
  const homeMarqueeContent = useRef(HTMLElement);
  const homeCards = useRef(HTMLElement);
  const homeCard1 = useRef(HTMLElement);
  const homeCard2 = useRef(HTMLElement);
  const homeCard3 = useRef(HTMLElement);
  const homeCard4 = useRef(HTMLElement);
  //plugins
  gsap.registerPlugin(SplitText);



  //marquee animation
  const marqueeTL = gsap.timeline();

  //marquee initial setup
  useEffect(() => {
    if (!homeMarquee || !homeMarqueeContent) return;
    const marqueeContentClone = homeMarqueeContent.current.cloneNode(true);
    homeMarquee.current.append(marqueeContentClone);
    const marqueeContentWidth = parseInt(
      getComputedStyle(homeMarqueeContent.current).getPropertyValue("width"),
      10
    );
    setMarqueeWidth(marqueeContentWidth);
    marqueeTL.to(homeMarquee.current, {
      duration: 0.5,
      scale: 1,
      opacity: 1,
      ease: "ease",
      delay: 0.8,
      onComplete: () => {
        setEnabled(true);
      },
    }, 0);
  }, []);

  //marquee trigger
  useEffect(() => {
    const homeMarqueeID = document.getElementById('homeMarquee')
    const playMarquee = () => {
      marqueeTL.fromTo(homeMarqueeID.children, {
        x: 0,
      }, {
        duration: 30,
        x: marqueeWidth * -1,
        ease: "none",
        repeat: -1,
      }, 0);
    }
    const handleResize = () => {
      if (homeMarqueeID) {
        const marqueeContentWidth = parseInt(
          getComputedStyle(homeMarqueeID.children[0]).getPropertyValue("width"),
          10
        );
        setMarqueeWidth(marqueeContentWidth);
        playMarquee();
      }
    }
    handleResize();
    window.addEventListener('resize', handleResize);
  }, [marqueeWidth]);





  // ctx implimentation-------------------------------------------------------------------------





  const getHomeCardsIntroTL = () => {
    const tl = gsap.timeline();
    //cards animation
    tl.to(homeCards.current, {
      duration: 0.8,
      y: 0,
      rotate: 0,
      opacity: 1,
      ease: 'ease',
    }, 0)
    tl.to(homeCard4.current, {
      duration: 0.8,
      ease: 'ease',
      rotate: '-30deg',
      delay: 0,
    }, 0)
    tl.to(homeCard3.current, {
      duration: 0.8,
      ease: 'ease',
      rotate: '-20deg',
      delay: 0,
    }, 0)
    tl.to(homeCard2.current, {
      duration: 0.8,
      ease: 'ease',
      rotate: '-10deg',
      delay: 0,
    }, 0)
    return tl;
  }


  const getMouseMoveTL = (event) => {
    let xPos = event.clientX / window.innerWidth - 0.5,
      yPos = event.clientY / window.innerHeight - 0.5;
    const tl = gsap.timeline();
    tl.to(homeCard1.current, {
      duration: 0.5,
      rotationY: xPos * 50,
      rotationX: yPos * -50,
      rotate: xPos * 40,
      y: yPos * 400,
      x: xPos * 400,
    }, 0)
    tl.to(homeCard2.current, {
      duration: 0.5,
      rotationY: xPos * 50,
      rotationX: yPos * -50,
      rotate: xPos * 30,
      y: yPos * 300,
      x: xPos * 300,
    }, 0)
    tl.to(homeCard3.current, {
      duration: 0.5,
      rotationY: xPos * 50,
      rotationX: yPos * -50,
      rotate: xPos * 20,
      y: yPos * 200,
      x: xPos * 200,
    }, 0)
    tl.to(homeCard4.current, {
      duration: 0.5,
      rotationY: xPos * 50,
      rotationX: yPos * -50,
      rotate: xPos * 10,
      y: yPos * 100,
      x: xPos * 100,
    }, 0)
    tl.to(homeMarquee.current, {
      duration: 0.5,
      rotate: xPos * 10,
    }, 0)
    return tl;
  }


  useLayoutEffect(() => {


    //gsap animations
    const ctx = gsap.context((context) => {
      getHomeCardsIntroTL();
      context.add('mouseMoveAnim', (event) => {
        getMouseMoveTL(event);
      })
    })



    window.addEventListener('mousemove', ctx.mouseMoveAnim);

    return () => {
      ctx.revert();
      window.removeEventListener('mousemove', ctx.mouseMoveAnim);
    };
  }, [])


  return (
    <Link className='home' to="/showcase" exact='true'>
      <div className='home-cards-container'>
        <div ref={homeCards} className='home-cards'>
          <div className='home-card-container'>
            <div ref={homeCard4} className='home-card'>
              <img className='about-headshot' key={Media[3].key} src={Media[3].src} />
            </div>
          </div>
          <div className='home-card-container'>
            <div ref={homeCard3} className='home-card'>
              <img className='about-headshot' key={Media[2].key} src={Media[2].src} />
            </div>
          </div>
          <div className='home-card-container'>
            <div ref={homeCard2} className='home-card'>
              <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
            </div>
          </div>
          <div className='home-card-container'>
            <div ref={homeCard1} className='home-card'>
              <img className='about-headshot' key={Media[0].key} src={Media[0].src} />
            </div>
          </div>
        </div>
      </div>
      <div className='home-marquee-container'>
        <div id='homeMarquee' ref={homeMarquee} className='home-marquee'>
          <div ref={homeMarqueeContent} className='home-marquee-content'>
            <span className='home-marquee-text'>
              designer
            </span>
            <span className='home-marquee-text'>
              -
            </span>
            <span className='home-marquee-text'>
              developer
            </span>
            <span className='home-marquee-text'>
              -
            </span>
            <span className='home-marquee-text'>
              creator
            </span>
            <span className='home-marquee-text'>
              -
            </span>
            <span className='home-marquee-text'>
              director
            </span>
            <span className='home-marquee-text'>
              -
            </span>
          </div>
        </div>
      </div>
      <div className='home-info-container'>
        <h2 className='home-info-description'>
          Jaime Castaneda <span className='home-info-description-dark'>Folio’24</span>
        </h2>
        <div className='home-info-footer'>
          <Instruction />
          <Location />
        </div>
      </div>
    </Link >
  );
}

export default Home;
