import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
//styles
import '../styles/link.css';
import '../styles/App.css';

const Link = ({ header }) => {
    gsap.registerPlugin(SplitText);
    let link = useRef(null);
    let linkButton = useRef(null);
    let linkButtonBorder = useRef(null);
    let linkButtonText = useRef(null);
    let linkButtonIcon = useRef(null);


    console.log(header)

    useEffect(() => {
        const linkTL = gsap.timeline();
        // parentDiv.addEventListener('mouseenter', () => { linkTL.play() })
        // parentDiv.addEventListener('mouseleave', () => { linkTL.reverse() })
    });


    // onClick = {() => { toggleModal(); }}

    return (
        <div className='link' ref={el => link = el}>
            <div className='link-header'>
                {header}
            </div>
            <div ref={el => linkButton = el} className="link-icon"  >
                <div ref={el => linkButtonBorder = el} className="link-icon-border" />
                <div ref={el => linkButtonIcon = el} className="link-icon-vector">
                    <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M1.03906 1.05957H18.9378V18.9571" stroke="#ECECEC" strokeWidth="2" strokeMiterlimit="10" />
                        <path d="M1.03906 18.9571L18.9378 1.05957" stroke="#ECECEC" strokeWidth="2" strokeMiterlimit="10" />
                    </svg>
                </div>
                <div ref={el => linkButtonText = el}>
                    <p className="link-icon-text">
                        Let's get it
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Link;
