import React, { useEffect, useRef } from 'react';
import { gsap } from "gsap";
//components
import Header from '../../components/header.react';
//styles
import '../../styles/loader.css';

const Loader = () => {

    //counter refs
    let loaderNumber100 = useRef(null);
    let loaderNumber88 = useRef(null);
    let loaderNumber22 = useRef(null);
    let loaderNumber0 = useRef(null);
    let loaderNumberPercent = useRef(null);
    //loader info refs
    let loaderInfo1 = useRef(null);
    let loaderInfo2 = useRef(null);
    let loaderInfo3 = useRef(null);

    useEffect(() => {
        var tl = gsap.timeline();
        tl.to(loaderInfo1, { delay: 1, opacity: 1, duration: 0.8 })
        tl.to(loaderInfo2, { opacity: 1, duration: 0.8 })
        tl.to(loaderInfo3, { opacity: 1, duration: 0.8 })
    })

    return (
        <div id="loader" className='loader'>
            <div className='loader-counter'>
                <h1 className='loader-number'>
                    <span ref={el => loaderNumber100 = el} className='loader-number-step'>
                        100
                    </span>
                    <span ref={el => loaderNumber88 = el} className='loader-number-step'>
                        88
                    </span>
                    <span ref={el => loaderNumber22 = el} className='loader-number-step'>
                        22
                    </span>
                    <span ref={el => loaderNumber0 = el} className='loader-number-step'>
                        0
                    </span>
                </h1>
                <h1 ref={el => loaderNumberPercent = el} className='loader-percent'>
                    %
                </h1>
            </div>
            <div className='loader-info'>
                <div ref={loaderInfo1} className='loader-info-left'>
                    <h2>
                        Jaime Castaneda
                    </h2>
                </div>
                <div ref={loaderInfo2} className='loader-info-center'>
                    <h2>
                        Designer + Developer
                    </h2>
                </div>
                <div ref={loaderInfo3} className='loader-info-right'>
                    <h2>
                        Portfolio Â©{new Date().getFullYear()}
                    </h2>
                </div>
            </div>
        </div>
    );
}

export default Loader;
