import React from 'react';
import { gsap } from 'gsap';
import { SwitchTransition, Transition } from 'react-transition-group';
import { useLocation } from "react-router-dom"

const About = ({ children }) => {
    const location = useLocation();
    return (

        <SwitchTransition>
            <Transition key={location.pathname}
                timeout={500}
                onEnter={(node) => {
                    console.log("enter ")
                    gsap.set(node, { autoAlpha: 0, scale: 0.8, xPercent: -100 });
                    gsap.to(node, { autoAlpha: 1, xPercent: 0, duration: 0.25 })
                        .play();
                }}
                onExit={(node) => {
                    console.log("exit ")
                    gsap
                        .to(node, { scale: 0.8, duration: 0.2 })
                        .to(node, { xPercent: 100, autoAlpha: 0, duration: 0.2 })
                        .play();
                }}>
                <div>
                    {children}
                </div>
            </Transition>
        </SwitchTransition>


    );
}

export default About;
