import { React, useLayoutEffect, useRef } from 'react';
import { gsap } from 'gsap';
//styles
import '../styles/App.css';


function Cursor() {
    let cursor = useRef(HTMLElement)


    useLayoutEffect(() => {
        // const handleMouseMove = e => {
        //     const { clientX: x, clientY: y } = e;
        //     const tl = gsap.timeline();
        //     tl.to(cursor.current, {
        //         top: x,
        //         left: y,
        //     },
        //     )
        //     return tl;
        // }

        const ctx = gsap.context((context) => {
            context.add('mouseMoveAnim', (e) => {
                console.log("mouse")
                const { clientX: x, clientY: y } = e;
                gsap.to(cursor.current, {
                    top: x,
                    left: y,
                },
                )
            })
        })


        window.addEventListener('mousemove', (event) => ctx.mouseMoveAnim(event));
        return () => { window.removeEventListener('mousemove', (event) => ctx.mouseMoveAnim(event)); }
    }, [])



    return (
        <div ref={el => cursor = el} className="cursor" />
    );
}

export default Cursor;
