import { React, useState } from 'react';
import { gsap } from 'gsap';
import { SwitchTransition, Transition, CSSTransition } from 'react-transition-group';
import { useLocation } from "react-router-dom"

const TransitionTrigger = ({ children }) => {
    const location = useLocation();
    const [isActive, setIsActive] = useState(false);
    return (

        <CSSTransition key={location.pathname}
            in
            timeout={3000}
            unmountOnExit
            onEnter={() => {
                setIsActive(true)
                console.log('enter', "isActive" + isActive)

            }}
            onExit={() => {
                setIsActive(false)
                console.log('exit', "isActive" + isActive)
            }}>
            {children}
        </CSSTransition>

    );
}

export default TransitionTrigger;
