import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
import { SwitchTransition, Transition } from "react-transition-group";
//styles
import '../styles/header.css';
import '../styles/App.css';


const CustomTransition = ({ children, timeout, onEnter, onExit }) => {
  const nodeRef = useRef(null);

  const handleEnter = () => {
    if (onEnter) {
      onEnter(nodeRef.current);
    }
  };

  const handleExit = () => {
    if (onExit) {
      onExit(nodeRef.current, () => {
        // The remove method is used to properly unmount the component
        // after the exit animation is complete
        nodeRef.current.remove();
      });
    }
  };

  return (
    <Transition
      nodeRef={nodeRef}
      timeout={timeout}
      onEnter={handleEnter}
      onExit={handleExit}
    >
      {children}
    </Transition>
  );
};

const Header = ({ headerContent }) => {
  gsap.registerPlugin(SplitText);
  let header = useRef(null);

  let animationNode = useRef(null);

  useEffect(() => {

    var tl = gsap.timeline(),
      mySplitText = new SplitText(header, { type: "chars" }),
      chars = mySplitText.chars; //an array of all the divs that wrap each character

    tl.from(chars, {
      duration: 0.8,
      opacity: 0,
      y: 200,
      rotationX: 0,
      ease: "ease",
      stagger: 0.1
    });

  }, [header])

  const animateIn = (node) => {
    gsap.from(node, { opacity: 0, y: -20, duration: 0.3 });
  };

  const animateOut = (node, done) => {
    gsap.to(node, { opacity: 0, y: -20, duration: 0.3, onComplete: done });
  };


  return (

    <div className='header-container'>
      <h1 className='header'>
        <div ref={el => header = el}>
          {headerContent}
        </div>
      </h1>

      <SwitchTransition>
        <CustomTransition key="about-paragraph" timeout={300} onEnter={animateIn()} onExit={animateOut()}>
          <p ref={el => animationNode = el}>This paragraph will animate with GSAP when switching.</p>
        </CustomTransition>
      </SwitchTransition>
    </div>

  );
}

export default Header;



