import { React, useEffect, useState, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
import {
    Link,
    Outlet,
} from "react-router-dom"
//components
import Header from '../../components/header.react';
//styles
import '../../styles/home.css';
import '../../styles/showcase.css';
//assets
import { Media } from "../../media/media";

const Showcase = () => {
    //refs
    let showcase = useRef(null);


    useEffect(() => {
        let ctx = gsap.context(() => {
            //define timeline
            const tl = gsap.timeline(),
                loaderHeaderSplit = new SplitText(loaderHeader, { type: "chars" }),
                loaderHeaderChars = loaderHeaderSplit.chars;

            tl.from(loaderHeaderChars, {
                duration: 0.5,
                opacity: 0,
                y: '25%',
                ease: "ease",
                stagger: 0.05,
                delay: 0.5,
            });
        })

    }, []);

    return (
        <div id="showcase" ref={el => showcase = el}>
            <div className='showcase-header'>
                <h1>
                    Showcase
                </h1>
                <div className='showcase-header-ui'>
                    <p>
                        Carousel - List
                    </p>
                </div>
            </div>
            <div className='showcase-carousel'>
                <Link to="project1">
                    Project 1
                </Link>
                <Link to="project2">
                    Project 2
                </Link>
            </div>
            <Outlet />
        </div>

    );
}

export default Showcase;
