        const spacing = 0.12;

        //timeline setup
        const sideScrollTimeline = element => {
            const sideScrollTL = gsap.timeline();
            sideScrollTL.fromTo(
                element,
                {
                    x: '100vw'
                },
                {
                    x: '-25vw', duration: 1, ease: "none", immediateRender: false
