import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { Media } from '../../media/media';
import '../../styles/showcase.css';

const Showcase = () => {
    // Refs
    const showcaseCarouselContainer = useRef(null);

    useEffect(() => {
        gsap.registerPlugin(ScrollTrigger);

        // Calculate carousel width
        const carouselWidth = showcaseCarouselContainer.current.offsetWidth;
        const carouselCardsWidth = showcaseCarouselContainer.current.children[0].offsetWidth;

        // Calculate distance to travel on scroll
        const carouselTravel = carouselWidth - carouselCardsWidth;

        // Smooth scroll function
        const smoothScroll = (distance, direction) => {
            gsap.to(showcaseCarouselContainer.current, {
                x: `+=${distance * direction}`,
                duration: 1,
                ease: 'power4.out',
            });
        };

        // Scroll trigger
        ScrollTrigger.create({
            trigger: showcaseCarouselContainer.current,
            start: 'top top',
            end: () => ScrollTrigger.maxScroll(window),
            onToggle: self => {
                const direction = self.direction === 1 ? -1 : 1; // -1 for up, 1 for down
                smoothScroll(carouselTravel, direction);
            },
        });

        // Fill carousel container
        const fillCarouselContainer = () => {
            const fill = Math.ceil(window.innerWidth / carouselWidth);
            for (let i = 1; i <= fill; i++) {
                const clone = showcaseCarouselContainer.current.children[0].cloneNode(true);
                showcaseCarouselContainer.current.appendChild(clone);
            }
        };
        fillCarouselContainer();

        // Clean up
        return () => {
            ScrollTrigger.getAll().forEach(trigger => trigger.kill());
        };
    }, []);

    return (
        <div id="showcase" className="showcase">
            <div className="showcase-carousel-container" ref={showcaseCarouselContainer}>
                <div className="showcase-carousel-cards">
                    {Media.map((item, index) => (
                        <div className="showcase-carousel-card-container" key={index}>
                            <div className="showcase-carousel-card">
                                <img className="about-headshot" src={item.src} alt={item.alt} />
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Showcase;
