    var tl = gsap.timeline(),
      mySplitText = new SplitText(header, { type: "chars" }),
      chars = mySplitText.chars; //an array of all the divs that wrap each character

    tl.from(chars, {
      duration: 0.8,
      opacity: 0,
      y: 200,
      rotationX: 0,
      ease: "ease",
