import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

// Import your media data here
import { Media } from "../../media/media";

const Showcase = () => {
    const showcaseCarouselContainer = useRef(null);

    useEffect(() => {
        // Initialize GSAP ScrollTrigger
        gsap.registerPlugin(ScrollTrigger);

        // Function to fill the carousel container with clones
        const fillCarouselContainer = () => {
            const showcaseCarouselChildren = showcaseCarouselContainer.current.children;
            let fill = Math.max(2, Math.floor((window.innerWidth * 1.5) / showcaseCarouselContainer.current.offsetWidth));

            for (let i = 1; i <= fill; i++) {
                showcaseCarouselContainer.current.appendChild(showcaseCarouselChildren[i - 1].cloneNode(true));
            }
        }

        // Function to animate the carousel
        const animateCarousel = () => {
            gsap.to(showcaseCarouselContainer.current.children, {
                x: () => -showcaseCarouselContainer.current.offsetWidth,
                ease: "none",
                scrollTrigger: {
                    trigger: showcaseCarouselContainer.current,
                    start: "top top",
                    end: () => "+=" + showcaseCarouselContainer.current.offsetWidth,
                    scrub: true,
                    pin: true,
                    invalidateOnRefresh: true,
                }
            });
        }

        // Call functions to fill and animate the carousel
        fillCarouselContainer();
        animateCarousel();

        // Refresh ScrollTrigger when the window resizes
        const handleResize = () => {
            ScrollTrigger.refresh();
        };

        window.addEventListener('resize', handleResize);

        // Cleanup function
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    return (
        <div id="showcase" className='showcase'>
            <div className='showcase-container'>
                <div className='showcase-content'>
                    <div className='showcase-header-container'>
                        <h1 className='showcase-header'>Showcase</h1>
                        <div className='showcase-header-ui'>
                            <p>Carousel - List</p>
                        </div>
                    </div>
                    <div ref={showcaseCarouselContainer} className='showcase-carousel-container'>
                        {Media.map((item, index) => (
                            <div key={index} className='showcase-carousel-card-container'>
                                <div className='showcase-carousel-card'>
                                    <img className='about-headshot' src={item.src} alt={`Project ${index + 1}`} />
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className='showcase-project-container'>
                        <h2 className='showcase-project-number'>----016</h2>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Showcase;
