import { React, useState } from 'react';
import { gsap } from 'gsap';
import { SwitchTransition, Transition, CSSTransition } from 'react-transition-group';
import { useLocation } from "react-router-dom"

const TransitionTrigger = ({ children }) => {
    const location = useLocation();
    //const [isActive, setIsActive] = useState(false);
    return (
        // <SwitchTransition>
        //     <Transition key={location.pathname}
        //         timeout={3000}
        //         onEnter={() => {
        //             console.log('enter', "isActive" + isActive)
        //             setIsActive((prevIsActive) => !prevIsActive)

        //         }}
        //         onExit={() => {
        //             console.log('exit', "isActive" + isActive)
        //             setIsActive((prevIsActive) => !prevIsActive)
        //         }}>
        //         {children}
        //     </Transition>
        // </SwitchTransition >

        <SwitchTransition>
            <Transition
                key={location.pathname}
                timeout={500}
                onEnter={(node) => {
                    console.log(node)
                    gsap.set(node, { autoAlpha: 0, scale: 0.8, xPercent: -100 });
                    gsap
                        .timeline({ paused: true })
                        .to(node, { autoAlpha: 1, xPercent: 0, duration: 0.25 })
                        .to(node, { scale: 1, duration: 0.25 })
                        .play();
                }}
                onExit={(node) => {
                    gsap
                        .timeline({ paused: true })
                        .to(node, { scale: 0.8, duration: 0.2 })
                        .to(node, { xPercent: 100, autoAlpha: 0, duration: 0.2 })
                        .play();
                }}
            >
                {children}
            </Transition>
        </SwitchTransition>
    );
}

export default TransitionTrigger;
