import React, { useLayoutEffect, useRef, useState, useMemo, useEffect } from 'react';
import { gsap } from 'gsap';
import { useLocation } from 'react-router-dom';
//styles
import '../styles/instruction.css';

const Instruction = ({ instruction }) => {
    //variables
    const location = useLocation();
    //state
    const [active, setActive] = useState(true);
    const [path, setPath] = useState(location.pathname);
    //refs
    const instructionText = useRef(HTMLElement);
    const instructionBrackets = useRef(HTMLElement);

    const getInstructionTL = () => {
        const tl = gsap.timeline();
        tl.to(instructionText.current,
            {
                duration: 0.5,
                opacity: 1,
                delay: 0.3,
            }, 0
        )
        tl.to(instructionBrackets.current,
            {
                duration: 0.5,
                width: '100%',
            }, 0
        )
        return tl;
    }

    // useEffect(() => {
    //     console.log("location change")
    //     if (path !== location.path) {
    //         setActive(false);
    //     } else {
    //         setActive(true);
    //     }
    // }, [location]);

    //intro animations
    useLayoutEffect(() => {
        const ctx = gsap.context((context) => {
            getInstructionTL();
        })

        console.log(ctx)

        return () => {
            ctx.revert();
        };
    }, [active])

    return (
        <div className='instruction'>
            <p className='instruction-text' ref={instructionText}>
                {instruction}
            </p>
            <div className='instruction-brackets' ref={instructionBrackets}>
                <p className='instruction-bracket-left'>
                    [
                </p>
                <p className='instruction-bracket-right'>
                    ]
                </p>
            </div>
        </div >
    );
}

export default Instruction;
