        let ctx = gsap.context(() => {
            //define timeline
            const showcaseIntroTL = gsap.timeline(),
                showcaseHeaderSplit = new SplitText(showcaseHeader, { type: "chars" }),
                showcaseHeaderChars = showcaseHeaderSplit.chars;

            showcaseIntroTL.to(showcaseHeaderChars, {
                duration: 0.5,
                opacity: 1,
                y: 0,
                ease: "ease",
                stagger: 0.05
            });
        })
    }, []);

    // //set up carousel
