import { React, useEffect, useState } from 'react';
import { SwitchTransition, Transition, CSSTransition } from 'react-transition-group';
import { useLocation } from "react-router-dom"
//components
import Header from '../components/header.react';
//styles
import '../styles/home.css';

const Home = () => {
  const location = useLocation();
  const [isActive, setIsActive] = useState(true);

  return (
    <SwitchTransition>
      <Transition key={location.pathname}
        timeout={3000}
        onEnter={() => {
          console.log('enter', "isActive" + isActive)
          setIsActive((prevIsActive) => !prevIsActive)

        }}
        onExit={() => {
          console.log('exit', "isActive" + isActive)
          setIsActive((prevIsActive) => !prevIsActive)
        }}>
        <div className='home'>
          <div className='home-header'>
            <Header headerContent="Hello, I'm Jaime" isActive={isActive} />
          </div>
        </div>
      </Transition>
    </SwitchTransition>
  );
}

export default Home;
