import React, { useEffect, useState } from "react";
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { ScrollToPlugin } from 'gsap/ScrollToPlugin';
import {
  Route,
  Routes,
  useLocation,
} from "react-router-dom"
//pages
import Loader from "./pages/loader/loader";
import Home from './pages/home/home';
import About from './pages/about/about';
import Showcase from './pages/showcase/showcase';
import Project1 from './pages/projects/project1';
import Project2 from './pages/projects/project2';
//components
import Nav from './components/nav.react'
//transitions
import TransitionTrigger from "./pages/transitionTrigger";
import { TransitionProvider } from "./context/transitionContext";
//assets
import { Media } from "./media/media";


// function preloadImage(src) {
//   return new Promise((resolve, reject) => {
//     const img = new Image()
//     img.onload = function () {
//       resolve(img)
//     }
//     img.onerror = img.onabort = function () {
//       reject(src)
//     }
//     img.src = src
//   })
// }

gsap.registerPlugin(ScrollTrigger, ScrollToPlugin);

function App() {
  const location = useLocation();
  const [mediaLoaded, setMediaLoaded] = useState(false)
  console.log(Media)

  useEffect(() => {
    const loadImage = image => {
      return new Promise((resolve, reject) => {
        const loadImg = new Image()
        loadImg.src = image.url
        // wait 2 seconds to simulate loading time
        loadImg.onload = () =>
          setTimeout(() => {
            resolve(image.url)
          }, 2000)

        loadImg.onerror = err => reject(err)
      })
    }

    Promise.all(Media.map(image => loadImage(image)))
      .then(() => setMediaLoaded(true))
      .catch(err => console.log("Failed to load images", err))
  }, [])

  // useEffect(() => {
  //   let isCancelled = false

  //   async function effect() {
  //     console.log('PRELOAD')

  //     if (isCancelled) {
  //       return
  //     }

  //     const imagesPromiseList = []
  //     for (const i of imageList) {
  //       imagesPromiseList.push(preloadImage(i))
  //     }

  //     await Promise.all(imagesPromiseList)

  //     if (isCancelled) {
  //       return
  //     }

  //     setAssetsLoaded(true)
  //   }

  //   effect()

  //   return () => {
  //     isCancelled = true
  //   }
  // }, [imageList])

  return (
    <>
      <Nav location={location} />
      <TransitionProvider>
        <Routes location={location}>
          <Route index path="/" exact element={<TransitionTrigger><Home /></TransitionTrigger>} />
          <Route path="about" exact element={<TransitionTrigger><About /></TransitionTrigger>} />
          <Route path="showcase" exact element={<TransitionTrigger><Showcase /></TransitionTrigger>}>
            <Route path="project1" element={<Project1 />} />
            <Route path="project2" element={<Project2 />} />
          </Route>
        </Routes>
      </TransitionProvider>
    </>
  );

}

export default App;
