import { React, useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
import { Draggable } from "gsap/Draggable";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import {
    Link,
    Outlet,
} from "react-router-dom"
//components
import Location from '../../components/location.react';
import Instruction from '../../components/instruction.react';
//styles
import '../../styles/home.css';
import '../../styles/showcase.css';
//assets
import { Media } from "../../media/media";

const Showcase = () => {
    //refs
    let showcase = useRef(null);
    let showcaseHeader = useRef(HTMLElement);
    let showcaseCarouselContainer = useRef(HTMLElement);
    let showcaseCarouselCards = useRef(HTMLElement);
    let showcaseCarouselDragProxy = useRef(null);
    //plugins
    gsap.registerPlugin(SplitText, Draggable, ScrollTrigger);


    // START CONST 

    gsap.set('.cards li', { xPercent: 400, opacity: 0, scale: 0 });

    const spacing = 0.1, // spacing of the cards (stagger)
        snapTime = gsap.utils.snap(spacing), // we'll use this to snapTime the playhead on the seamlessLoop
        cards = gsap.utils.toArray('.cards li'),
        // this function will get called for each element in the buildSeamlessLoop() function, and we just need to return an animation that'll get inserted into a master timeline, spaced
        animateFunc = element => {
            const tl = gsap.timeline();
            tl.fromTo(element, { scale: 0, opacity: 0 }, { scale: 1, opacity: 1, zIndex: 100, duration: 0.5, yoyo: true, repeat: 1, ease: "power1.in", immediateRender: false })
                .fromTo(element, { xPercent: 400 }, { xPercent: -400, duration: 1, ease: "none", immediateRender: false }, 0);
            return tl;
        },
        seamlessLoop = buildSeamlessLoop(cards, spacing, animateFunc),
        playhead = { offset: 0 }, // a proxy object we use to simulate the playhead position, but it can go infinitely in either direction and we'll just use an onUpdate to convert it to the corresponding time on the seamlessLoop timeline.
        wrapTime = gsap.utils.wrap(0, seamlessLoop.duration()), // feed in any offset (time) and it'll return the corresponding wrapped time (a safe value between 0 and the seamlessLoop's duration)
        scrub = gsap.to(playhead, { // we reuse this tween to smoothly scrub the playhead on the seamlessLoop
            offset: 0,
            onUpdate() {
                seamlessLoop.time(wrapTime(playhead.offset)); // convert the offset to a "safe" corresponding time on the seamlessLoop timeline
            },
            duration: 0.5,
            ease: "power3",
            paused: true
        }),
        trigger = ScrollTrigger.create({
            start: 0,
            onUpdate(self) {
                let scroll = self.scroll();
                if (scroll > self.end - 1) {
                    wrap(1, 2);
                } else if (scroll < 1 && self.direction < 0) {
                    wrap(-1, self.end - 2);
                } else {
                    scrub.vars.offset = (iteration + self.progress) * seamlessLoop.duration();
                    scrub.invalidate().restart(); // to improve performance, we just invalidate and restart the same tween. No need for overwrites or creating a new tween on each update.
                }
            },
            end: "+=3000",
            pin: ".gallery"
        }),
        // converts a progress value (0-1, but could go outside those bounds when wrapping) into a "safe" scroll value that's at least 1 away from the start or end because we reserve those for sensing when the user scrolls ALL the way up or down, to wrap.
        progressToScroll = progress => gsap.utils.clamp(1, trigger.end - 1, gsap.utils.wrap(0, 1, progress) * trigger.end),
        wrap = (iterationDelta, scrollTo) => {
            iteration += iterationDelta;
            trigger.scroll(scrollTo);
            trigger.update(); // by default, when we trigger.scroll(), it waits 1 tick to update().
        };















    //intro animations
    useEffect(() => {
        let ctx = gsap.context(() => {
            //define timeline
            const showcaseIntroTL = gsap.timeline(),
                showcaseHeaderSplit = new SplitText(showcaseHeader, { type: "chars" }),
                showcaseHeaderChars = showcaseHeaderSplit.chars;

            showcaseIntroTL.to(showcaseHeaderChars, {
                duration: 0.5,
                opacity: 1,
                y: 0,
                ease: "ease",
                stagger: 0.05
            });
        })
    }, []);


















    //set up draggable
    useEffect(() => {
        Draggable.create(showcaseCarouselCards, {
            type: "x",
            //bounds: document.getElementById("container"),
            inertia: true,
            onClick: function () {
                console.log("clicked");
            },
            onDragEnd: function () {
                console.log("drag ended");
            },
        });
    }, []);









    return (
        <div id="showcase" ref={el => showcase = el}>
            <div className='showcase-header-container'>
                <h1 ref={el => showcaseHeader = el} className='showcase-header'>
                    Showcase
                </h1>
                <div className='showcase-header-ui'>
                    <p>
                        Carousel - List
                    </p>
                </div>
            </div>
            <div ref={el => showcaseCarouselContainer = el} className='showcase-carousel-container'>
                <div ref={el => showcaseCarouselCards = el} className='showcase-carousel-cards'>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
                        </div>
                    </div>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
                        </div>
                    </div>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
                        </div>
                    </div>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
                        </div>
                    </div>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
                        </div>
                    </div>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
                        </div>
                    </div>
                    <div className='showcase-carousel-card-container'>
                        <div className='showcase-carousel-card'>
                            <img className='about-headshot' key={Media[1].key} src={Media[1].src} />
