import { React, useLayoutEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from "gsap/ScrollTrigger";
//styles
import '../styles/cta.css';

const CTA = () => {
    //refs
    const cta = useRef(HTMLElement);
    const ctaHeaderLine = useRef(HTMLElement);
    const ctaHeaderTextLeft = useRef(HTMLElement);
    const ctaHeaderTextRight = useRef(HTMLElement);
    const ctaConnectHeader = useRef(HTMLElement);
    const ctaConnectLocale = useRef(HTMLElement);
    const ctaConnectSocials = useRef(HTMLElement);
    const ctaConnectPhone = useRef(HTMLElement);
    const ctaConnectEmail = useRef(HTMLElement);
    //plugins
    gsap.registerPlugin(ScrollTrigger);

    //activate animation
    const getActiveTL = () => {
        const tl = gsap.timeline()
        tl.to(cta.current, {
            backgroundColor: "#ececec",
            duration: 0.5,
            ease: 'ease',
        }, 0).to(ctaHeaderLine.current, {
            width: "33.3%",
            backgroundColor: "#303030",
            duration: 0.5,
            ease: 'ease',
        }, 0).to([ctaHeaderTextLeft.current, ctaHeaderTextRight.current, ctaConnectHeader.current, ctaConnectLocale.current, ctaConnectSocials.current, ctaConnectPhone.current, ctaConnectEmail.current], {
            color: "#303030",
            duration: 0.5,
            ease: 'ease',
        }, 0)





        tl.pause();
        return tl;
    }

    //intro 
    useLayoutEffect(() => {
        const ctx = gsap.context((context) => {
            let tl = getActiveTL();
            ScrollTrigger.create({
                start: 0,
                end: () => ScrollTrigger.maxScroll(window) - 2,
                onLeave: () => { tl.play() },
                onEnterBack: () => { tl.reverse() }
            });
        })
    }, [])

    return (
        <div ref={cta} className='cta'>
            <div className='cta-container'>
                <div className='cta-content'>
                    <div className='cta-header'>
                        <div className='cta-header-text-left'>
                            <h1 ref={ctaHeaderTextLeft} >
                                2024
                            </h1>
                        </div>
                        <div ref={ctaHeaderLine} className='cta-header-line' />
                        <div className='cta-header-text-right'>
                            <h1 ref={ctaHeaderTextRight}>
                                Folio
                            </h1>
                        </div>
                    </div>
                    <div className='cta-connect'>
                        <h2 ref={ctaConnectHeader} className='cta-connect-header'>
                            [ Let's Connect ]
                        </h2>
                        <div className='cta-connect-info'>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Locale
                                </h4>
                                <p ref={ctaConnectLocale}>
                                    SF, Bay Area
                                </p>
                            </div>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Socials
                                </h4>
                                <p ref={ctaConnectSocials}>
                                    @the_casta_way
                                </p>
                            </div>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Phone
                                </h4>
                                <p ref={ctaConnectPhone}>
                                    +1 408 828 3020
                                </p>
                            </div>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Email
                                </h4>
                                <p ref={ctaConnectEmail}>
                                    hello@jaimecastaneda.com
                                </p>
                            </div>
                        </div>
                    </div>

                </div >

            </div >

        </div >
    );
}

export default CTA;
