  //     },
  //   }, 0);

  //   //marquee animation
  //   const playMarquee = () => {
  //     getMarqueeWidth();
  //     if (homeMarquee.children === null) {
  //       return
  //     } else {
  //       marqueeTL.fromTo(homeMarquee.children, {
  //         x: 0,
  //       }, {
  //         duration: 5,
  //         x: marqueeWidth * -1,
  //         ease: "none",
  //         repeat: -1,
  //       }, 0);

  //       console.log(marqueeWidth, homeMarquee.children)
  //     }
  //   }

  //   playMarquee();

  //   window.addEventListener('resize', playMarquee);
  //   // return () => {
  //   //   window.addEventListener('resize', getMarqueeWidth());
  //   // }
  // }, [marqueeWidth]);



  //marquee trigger
  useEffect(() => {
    if (!homeMarquee || !homeMarqueeContent) return;
    const marqueeContentClone = homeMarqueeContent.cloneNode(true);
    homeMarquee.append(marqueeContentClone);


    const marqueeTL = gsap.timeline();
    marqueeTL.to(homeMarquee, {
      duration: 0.5,
      scale: 1,
      opacity: 1,
      ease: "ease",
      delay: 0.8,
      onComplete: () => {
        setEnabled(true);
      },
    }, 0);

    //marquee animation
    const playMarquee = () => {
      getMarqueeWidth();
      if (homeMarquee.children === null) {
        return
      } else {
        marqueeTL.fromTo(homeMarquee.children, {
          x: 0,
        }, {
          duration: 5,
          x: marqueeWidth * -1,
          ease: "none",
          repeat: -1,
