import React, { useLayoutEffect, useRef, useState } from 'react';
import { useNavigate } from "react-router-dom";
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
//components
import Footer from '../../components/footer.react';
//styles
import '../../styles/home.css';
//assets
import { ThumbnailMedia } from "../../media/media";

const Home = () => {
  //state
  const [enabled, setEnabled] = useState(false);
  //refs
  const homeHeader = useRef(HTMLElement);
  const homeHeaderTextLeft = useRef(HTMLElement);
  const homeHeaderTextRight = useRef(HTMLElement);
  const homeHeaderLine = useRef(HTMLElement);
  const homeCards = useRef(HTMLElement);
  const homeCard1 = useRef(HTMLElement);
  const homeCard2 = useRef(HTMLElement);
  const homeCard3 = useRef(HTMLElement);
  const homeCard4 = useRef(HTMLElement);
  const homeInfoContainer = useRef(HTMLElement);
  //variables
  const navigate = useNavigate();
  //plugins
  gsap.registerPlugin(SplitText);

  //home cards intro animation timeline
  const getHomeCardsIntroTL = () => {
    const tl = gsap.timeline();
    tl.to(homeCards.current, {
      duration: 0.8,
      y: 0,
      rotate: 0,
      ease: 'ease',
    }, 0)
    tl.to(homeCards.current, {
      duration: 0.3,
      opacity: 1,
      ease: 'ease',
    }, 0)
    tl.to(homeCard4.current, {
      duration: 0.8,
      ease: 'ease',
      rotate: '-30deg',
      delay: 0,
    }, 0)
    tl.to(homeCard3.current, {
      duration: 0.8,
      ease: 'ease',
      rotate: '-20deg',
      delay: 0,
    }, 0)
    tl.to(homeCard2.current, {
      duration: 0.8,
      ease: 'ease',
      rotate: '-10deg',
      delay: 0,
      onComplete: () => {
        setEnabled(true);
      },
    }, 0)
    tl.from(homeInfoContainer.current, {
      duration: 0.8,
      opacity: 0,
      ease: 'ease',
      delay: 0.5,
    }, 0)
    return tl;
  }

  //mouse move parallax animation timeline
  const getMouseMoveTL = (event) => {
    let xPos = event.clientX / window.innerWidth - 0.5,
      yPos = event.clientY / window.innerHeight - 0.5;
    const tl = gsap.timeline();
    tl.to(homeCard1.current, {
      duration: 0.5,
      // rotationY: xPos * 50,
      // rotationX: yPos * -50,
      // rotationZ: xPos * 20,
      y: yPos * 200,
      x: xPos * 200,
      ease: "power1.Out"
    }, 0)
    tl.to(homeCard2.current, {
      duration: 0.5,
      // rotationY: xPos * 50,
      // rotationX: yPos * -50,
      // rotationZ: xPos * 10,
      y: yPos * 150,
      x: xPos * 150,
      ease: "power2.Out"
    }, 0)
    tl.to(homeCard3.current, {
      duration: 0.5,
      // rotationY: xPos * 50,
      // rotationX: yPos * -50,
      // rotationZ: xPos * 0,
      y: yPos * 100,
      x: xPos * 100,
      ease: "power3.Out"
    }, 0)
    tl.to(homeCard4.current, {
      duration: 0.5,
      // rotationY: xPos * 50,
      // rotationX: yPos * -50,
      // rotationZ: xPos * -10,
      y: yPos * 50,
      x: xPos * 50,
      ease: "power4.Out"
    }, 0)
    tl.to(homeHeader.current, {
      duration: 0.5,
      rotate: xPos * 10,
      ease: "power4.Out"
    }, 0)
    return tl;
  }

  //check for mobile
  const mobileAndTabletCheck = () => {
    let check = false;
