import React, { useLayoutEffect, useRef } from 'react';
import { gsap } from "gsap";
import { SplitText } from "gsap/SplitText";
//components
import Location from '../../components/location.react';
import Instruction from '../../components/instruction.react';
//styles
import '../../styles/loader.css';
//styles
import '../../styles/home.css';

const TIME = 4;

const Loader = () => {

    //refs
    const loaderCounter = useRef(HTMLElement);
    const loaderHeader = useRef(HTMLElement);
    const loaderLine = useRef(HTMLElement);

    //initialize gsap plugins
    gsap.registerPlugin(SplitText);

    //counter 
    let counter = {
        val: 0
    }

    useLayoutEffect(() => {
        let ctx = gsap.context(() => {
            //define timeline
            const tl = gsap.timeline();
            // loaderHeaderSplit = new SplitText(loaderHeader.current, { type: "chars" }),
            // loaderHeaderChars = loaderHeaderSplit.chars;

            // tl.from(loaderHeaderChars, {
            //     duration: 0.5,
            //     opacity: 0,
            //     y: '25%',
            //     ease: "ease",
            //     stagger: 0.05,
            //     delay: 0.5,
            // });
            tl.from(loaderCounter.current, {
                duration: 0.5,
                opacity: 0,
                ease: "ease",
                delay: -0.4,

            }, 0);
            tl.to(counter, {
                duration: TIME,
                val: 100,
                onUpdate() {
                    let counterVal = Math.ceil(counter.val)
                    if (counter.val <= 9) {
                        loaderCounter.current.textContent = '00' + counterVal;
                    }
                    else if (counter.val <= 99 && counter.val >= 9) {
                        loaderCounter.current.textContent = '0' + counterVal;
                    }
                    else {
                        loaderCounter.current.textContent = counterVal;
                    }
                },
                ease: 'power3.inOut',
                delay: 0.5,
            }, 1);
            tl.to(loaderCounter.current, {
                duration: 0.5,
                opacity: 0,
                y: '-25%',
                ease: "ease",
            }, 2);
            tl.to(loaderHeader.current, {
                transform: 'scale(1)',
                ease: "ease",
            }, 2);
            tl.to(loaderLine.current, {
                width: "75%",
                ease: "ease",
            }, 2);


            // tl.to(loaderHeaderChars, {
            //     duration: 0.5,
            //     opacity: 0,
            //     y: '-25%',
            //     ease: "ease",
            //     stagger: 0.05,
            //     delay: -0.5,
            // });
        })
        return () => ctx.revert();
    }, []);

    return (
        <div id="loader" className='loader'>
            <div className='loader-container'>
                <h1 className='loader-header' ref={loaderHeader}>
                    <span>
                        2024
                    </span>
                </h1>
                <div className='loader-line' ref={loaderLine} />
                <h1 className='loader-header' ref={loaderHeader}>
                    <span>
                        Folio
                    </span>
                </h1>
                <p className='loader-counter'>
                    <span ref={loaderCounter}>
                        000
                    </span>
                </p>
            </div >
            <div className='home-info-footer'>
                <Instruction />
                <Location />
            </div>
        </div >
    );
}

export default Loader;
