import { React, useLayoutEffect, useRef } from 'react';
import { gsap } from 'gsap';
import {
    Link
} from "react-router-dom"
//styles
import '../styles/archived.css';
//assets
import { Media } from "../media/media";

const Archived = ({ children, name }) => {
    //refs
    const archived = useRef(HTMLElement);
    const archivedHover = useRef(HTMLElement);










    //intro 
    useLayoutEffect(() => {
        const ctx = gsap.context((context) => {
            const tl = gsap.timeline();
            tl.to(archived, {
                height: 'auto',
                duration: 0.5,
                ease: 'ease',
            }, 0)











            //getHoverTL();

            // context.add('hoverAnim', (event) => {
            //     getHoverTL(event);
            // })
        })

        console.log(ctx)

        const handleMouseEnter = (event) => {
            ctx.hoverAnim(event).play();
        }

        const handleMouseLeave = (event) => {
            ctx.hoverAnim(event).reverse();
        }

        archived.current.addEventListener('mouseenter', handleMouseEnter)
        archived.current.addEventListener('mouseLeave', handleMouseLeave)

        return () => {
            ctx.revert();
            archived.current.removeEventListener('mouseenter', handleMouseEnter)
            archived.current.removeEventListener('mouseLeave', handleMouseLeave)
        };
    }, [])







    return (
        <div ref={archived} className='archived'>
            <div className='archived-content'>
                <div className='archived-info'>
                    <div className='archived-info-name'>
                        <h4>
                            Name
                        </h4>
                        <p>
                            {name}
                        </p>
                    </div>
                </div>
                <div className='archived-icon'>
                    <div className="archived-icon-vector">
                        <svg height={20} width={20} viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M1.03906 1.05957H18.9378V18.9571" stroke="#ECECEC" strokeWidth="2" strokeMiterlimit="10" />
                            <path d="M1.03906 18.9571L18.9378 1.05957" stroke="#ECECEC" strokeWidth="2" strokeMiterlimit="10" />
                        </svg>
                    </div>
                </div>
            </div>
            <div ref={archivedHover} className='archived-hover'>
                <div className='archived-hover-description'>
                    <h4>
                        Description
                    </h4>
                    {children}
                </div>

            </div>
        </div>

    )
}

export default Archived;
