import React, { useEffect, useRef } from 'react';
import { gsap } from "gsap";
import { SplitText } from "gsap/SplitText";
//components
import Header from '../../components/header.react';
//styles
import '../../styles/loader.css';

const Loader = () => {
    gsap.registerPlugin(SplitText);

    //counter refs
    let loaderNumber100 = useRef(null);
    let loaderNumber88 = useRef(null);
    let loaderNumber22 = useRef(null);
    let loaderNumber0 = useRef(null);
    let loaderNumberPercent = useRef(null);
    //loader info refs
    let loaderInfo1 = useRef(null);
    let loaderInfo2 = useRef(null);
    let loaderInfo3 = useRef(null);

    useEffect(() => {
        // var tl = gsap.timeline();
        // tl.to(loaderInfo1, { delay: 1, opacity: 1, duration: 0.8 })
        // tl.to(loaderInfo2, { opacity: 1, duration: 0.8 })
        // tl.to(loaderInfo3, { opacity: 1, duration: 0.8 })

        const tl = gsap.timeline(),
            loaderNumber0Split = new SplitText(loaderNumber0, { type: "chars" }),
            // loaderNumber22Split = new SplitText(loaderNumber22, { type: "chars" }),
            // loaderNumber88Split = new SplitText(loaderNumber88, { type: "chars" }),
            // loaderNumber100Split = new SplitText(loaderNumber100, { type: "chars" }),
            loaderNumberPercentSplit = new SplitText(loaderNumberPercent, { type: "chars" }),
            chars0 = loaderNumber0Split.chars,
            // chars22 = loaderNumber22Split.chars,
            // chars88 = loaderNumber88Split.chars,
            // chars100 = loaderNumber100Split.chars,
            charsPercent = loaderNumberPercentSplit.chars;

        tl.from(chars0, {
            duration: 0.8,
            opacity: 0,
            y: 200,
            rotationX: 0,
            ease: "ease",
            stagger: 0.1
        });

        tl.from(charsPercent, {
            duration: 0.8,
            opacity: 0,
            y: 200,
            rotationX: 0,
            ease: "ease",
            stagger: 0.1
        });

    })

    return (
        <div id="loader" className='loader'>
            <div className='loader-counter'>
                <h1 ref={el => loaderNumber0 = el} className='loader-number'>
                    {/* <span ref={el => loaderNumber100 = el} className='loader-number-step'>
                        100
                    </span>
                    <span ref={el => loaderNumber88 = el} className='loader-number-step'>
                        88
                    </span>
                    <span ref={el => loaderNumber22 = el} className='loader-number-step'>
                        22
                    </span> */}

                    0

                </h1>
                <h1 ref={el => loaderNumberPercent = el} className='loader-percent'>
                    %
                </h1>
            </div>
            <div className='loader-info'>
                <div ref={loaderInfo1} className='loader-info-left'>
                    <h2>
                        Jaime Castaneda
                    </h2>
                </div>
                <div ref={loaderInfo2} className='loader-info-center'>
                    <h2>
                        Designer + Developer
                    </h2>
                </div>
                <div ref={loaderInfo3} className='loader-info-right'>
                    <h2>
                        Portfolio Â©{new Date().getFullYear()}
                    </h2>
                </div>
            </div>
        </div>
    );
}

export default Loader;
