import { React, useLayoutEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from "gsap/ScrollTrigger";
//styles
import '../styles/cta.css';

const CTA = () => {
    //refs
    const ctaHeaderLine = useRef(HTMLElement);
    //plugins
    gsap.registerPlugin(ScrollTrigger);

    const getActiveTL = () => {
        const tl = gsap.timeline({
            scrollTrigger: {
                trigger: featuredContent.current,
                markers: false,
                pin: false, // pin the trigger element while active
                start: 'bottom bottom',
                end: () => ScrollTrigger.maxScroll(window) - 2,
                onLeave: (self) => { self.play() },
            }
        });

        return tl;
    }

    //intro 
    useLayoutEffect(() => {

        const ctx = gsap.context((context) => {
            getActiveTL();
        })
    }, [])

    return (
        <div className='cta'>
            <div className='cta-container'>
                <div className='cta-content'>
                    <div className='cta-header'>
                        <div className='cta-header-text-left'>
                            <h1>
                                2024
                            </h1>
                        </div>
                        <div ref={ctaHeaderLine} className='cta-header-line' />
                        <div className='cta-header-text-right'>
                            <h1>
                                Folio
                            </h1>
                        </div>
                    </div>
                    <div className='cta-connect'>
                        <h2 className='cta-connect-header'>
                            [ Let's Connect ]
                        </h2>
                        <div className='cta-connect-info'>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Name
                                </h4>
                                <p>
                                    Jaime Castaneda
                                </p>
                            </div>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Status
                                </h4>
                                <p>
                                    Designer / Developer
                                </p>
                            </div>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Occupation
                                </h4>
                                <p>
                                    Designer / Developer
                                </p>
                            </div>
                            <div className="cta-connect-info-text-node">
                                <h4>
                                    Currently
                                </h4>
                                <p>
                                    Designer / Engineer @ Meta
                                </p>
                            </div>
                        </div>
                    </div>

                </div >

            </div >

        </div >
    );
}

export default CTA;
