import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";
import { SwitchTransition, Transition } from "react-transition-group";
//styles
import '../styles/header.css';
import '../styles/App.css';

const Header = ({ headerContent }) => {
  gsap.registerPlugin(SplitText);
  let header = useRef(null);
  const titleRef = useRef(null);

  useEffect(() => {

    // var tl = gsap.timeline(),
    //   mySplitText = new SplitText(header, { type: "chars" }),
    //   chars = mySplitText.chars; //an array of all the divs that wrap each character

    // tl.from(chars, {
    //   duration: 0.8,
    //   opacity: 0,
    //   y: 200,
    //   rotationX: 0,
    //   ease: "ease",
    //   stagger: 0.1
    // });

    // return () => {
    //   tl.to(chars, {
    //     duration: 0.8,
    //     opacity: 0,
    //     y: 200,
    //     rotationX: 0,
    //     ease: "ease",
    //     stagger: 0.1
    //   });

    // }




  }, [])


  // useEffect(() => {
  //   const titleElement = titleRef.current;
  //   gsap.fromTo(titleElement, { opacity: 0 }, { opacity: 1, duration: 0.3 });

  //   return () => {
  //     gsap.to(titleElement, { opacity: 0, duration: 0.3 });
  //   };
  // }, []);


  return (

    <div className='header-container'>
      <h1 className='header'>
        <SwitchTransition>
          <Transition key={location.pathname}
            timeout={500}
            onEnter={(node) => {
              gsap.set(node, { autoAlpha: 0, scale: 0.8, xPercent: -100 });
              gsap
                .timeline({ paused: true })
                .to(node, { autoAlpha: 1, xPercent: 0, duration: 0.25 })
                .to(node, { scale: 1, duration: 0.25 })
                .play();
            }}
            onExit={(node) => {
              gsap
                .timeline({ paused: true })
                .to(node, { scale: 0.8, duration: 0.2 })
                .to(node, { xPercent: 100, autoAlpha: 0, duration: 0.2 })
                .play();
            }}
          >
            <div ref={el => header = el} >
              {headerContent}
            </div>
          </Transition>
        </SwitchTransition>
      </h1>
    </div>

  );
}

export default Header;
