import { React } from 'react';
//components
import Header from '../../components/header.react';
//styles
import '../../styles/home.css';
//assets
import Video from '../../media/home/home_sizzle.mp4'

import { gsap } from 'gsap';
import { SplitText } from "gsap/SplitText";

const Home = () => {
  //refs
  let homeMarquee = useRef(null);

  gsap.registerPlugin(SplitText);
  const header = node.querySelectorAll('.header');
  const video = node.querySelectorAll('.home-video-content');
  const tl = gsap.timeline(),
    mySplitText = new SplitText(header, { type: "chars" }),
    chars = mySplitText.chars; //an array of all the divs that wrap each character

  tl.from(chars, {
    duration: 0.4,
    opacity: 0,
    y: 100,
    ease: "ease",
    stagger: 0.05
  });
  tl.from(video, {
    duration: 0.4,
    delay: -0.3,
    opacity: 0,
    y: 100,
    ease: "ease",
  });

  return (
    <div className='home'>
      <div className='home-marquee-container'>
        <div className='home-marquee'>
          <span className='home-marquee-text'>
            Designer -
          </span>
          <span className='home-marquee-text'>
            Developer -
          </span>
          <span className='home-marquee-text'>
            UI Designer -
          </span>
          <span className='home-marquee-text'>
            Web Designer -
          </span>
        </div>
      </div>

    </div >
  );
}

export default Home;
