import React, { useContext } from 'react';
import { SwitchTransition, Transition } from 'react-transition-group';
import { useLocation } from "react-router-dom"
//transitionContext
import TransitionContext from '../context/transitionContext';
//transitions
import { HomeTransitionExit } from './home/homeTransition';
import { AboutTransitionExit } from './about/aboutTransition';
import { ShowcaseTransitionExit } from './showcase/showcaseTransition';

const TransitionTrigger = ({ children }) => {
    const location = useLocation();
    const { toggleEntered } = useContext(TransitionContext);
    let timeout;
    if (location.pathname === "/") {
        timeout = 200;
    }
    else if (location.pathname === "/about") {
        timeout = 200;
    }
    else if (location.pathname === "/showcase") {
        timeout = 200;
    }
    else { timeout = 200 }

    return (
        <>
            <SwitchTransition>
                <Transition
                    key={location.pathname}
                    timeout={timeout}
                    onEnter={() => {
                        toggleEntered(false);
                        console.log('enter')
                    }}
                    onEntered={(node) => {
                        toggleEntered(true);
                        console.log('entered')
                    }}
                    onExit={(node) => {
                        console.log('exit')
                        if (location.pathname === "/") {
                            HomeTransitionExit({ node });
                        }
                        else if (location.pathname === "/about") {
                            AboutTransitionExit({ node });
                        }
                        else if (location.pathname === "/showcase") {
                            ShowcaseTransitionExit({ node });
                        }
                    }}

                    onExiting={console.log('exiting')}
                    onExited={console.log('exited')}
                >
                    {children}
                </Transition>
            </SwitchTransition>
        </>
    );
}

export default TransitionTrigger;
